contracts/Coordinator.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { OApp, Origin, MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { OAppOptionsType3 } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OAppOptionsType3.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { WalletFactory } from "./WalletFactory.sol";
import { SessionModule } from "./SessionModule.sol";

/// @title Coordinator - Sepolia OApp for cross-chain session installation
/// @notice Coordinates session key installation across multiple chains via LayerZero
contract Coordinator is OApp, OAppOptionsType3 {
    using ECDSA for bytes32;

    /// @notice Message type for session installation
    uint16 public constant INSTALL_SESSION = 1;

    /// @notice The wallet factory for creating minimal wallets
    WalletFactory public immutable walletFactory;

    /// @notice EIP-712 domain separator
    bytes32 public immutable DOMAIN_SEPARATOR;

    /// @notice EIP-712 typehash for SessionIntent
    bytes32 public constant SESSION_INTENT_TYPEHASH = keccak256(
        "SessionIntent(address user,address sessionPubKey,uint256 expiry,uint256 nonce,Destination[] destinations,TokenPolicy[] tokenPolicies)Destination(uint32 chainEid,address escrowAddr)TokenPolicy(address token,uint256 limit)"
    );

    /// @notice EIP-712 typehash for Destination
    bytes32 public constant DESTINATION_TYPEHASH = keccak256(
        "Destination(uint32 chainEid,address escrowAddr)"
    );

    /// @notice EIP-712 typehash for TokenPolicy
    bytes32 public constant TOKEN_POLICY_TYPEHASH = keccak256(
        "TokenPolicy(address token,uint256 limit)"
    );

    /// @notice Destination chain configuration
    struct Destination {
        uint32 chainEid;      // LayerZero endpoint ID
        address escrowAddr;   // Escrow contract address on destination
    }

    /// @notice Token spending policy
    struct TokenPolicy {
        address token;        // Token address (address(0) for native)
        uint256 limit;        // Spending limit
    }

    /// @notice Session installation intent (EIP-712)
    struct SessionIntent {
        address user;                    // User's EOA address
        address sessionPubKey;           // Session public key
        uint256 expiry;                  // Session expiration timestamp
        uint256 nonce;                   // Replay protection nonce
        Destination[] destinations;      // Target chains and escrows
        TokenPolicy[] tokenPolicies;     // Token spending policies
    }

    /// @notice User nonces for replay protection
    mapping(address => uint256) public userNonces;

    /// @notice Events
    event SessionInstallationInitiated(
        address indexed user,
        address indexed sessionPubKey,
        uint256 expiry,
        uint32[] chainEids
    );
    event SessionInstalledLocally(address indexed user, address indexed wallet);

    /// @notice Errors
    error InvalidSignature();
    error InvalidNonce();
    error SessionExpired();
    error EmptyDestinations();
    error InvalidDestination();

    /// @notice Initialize Coordinator
    /// @param _endpoint LayerZero endpoint address
    /// @param _owner Contract owner
    /// @param _walletFactory Wallet factory contract
    constructor(
        address _endpoint,
        address _owner,
        WalletFactory _walletFactory
    ) OApp(_endpoint, _owner) Ownable(_owner) {
        walletFactory = _walletFactory;

        // EIP-712 domain separator
        DOMAIN_SEPARATOR = keccak256(abi.encode(
            keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
            keccak256(bytes("SessionCoordinator")),
            keccak256(bytes("1")),
            block.chainid,
            address(this)
        ));
    }

    /// @notice Install sessions across multiple chains
    /// @param intent The session installation intent
    /// @param signature EIP-712 signature from user
    /// @param options LayerZero execution options for each destination
    function installSessions(
        SessionIntent calldata intent,
        bytes calldata signature,
        bytes[] calldata options
    ) external payable {
        // Verify destinations
        if (intent.destinations.length == 0) revert EmptyDestinations();
        require(intent.destinations.length == options.length, "Mismatched options length");

        // Verify signature
        _verifySessionIntent(intent, signature);

        // Verify session not expired
        if (intent.expiry <= block.timestamp) revert SessionExpired();

        // Verify and increment nonce
        if (intent.nonce != userNonces[intent.user]) revert InvalidNonce();
        userNonces[intent.user]++;

        // Install session locally on Sepolia (optional)
        _installLocalSession(intent);

        // Send cross-chain messages to install sessions
        _sendCrossChainInstallations(intent, options);

        // Emit event
        uint32[] memory chainEids = new uint32[](intent.destinations.length);
        for (uint256 i = 0; i < intent.destinations.length; i++) {
            chainEids[i] = intent.destinations[i].chainEid;
        }

        emit SessionInstallationInitiated(
            intent.user,
            intent.sessionPubKey,
            intent.expiry,
            chainEids
        );
    }

    /// @notice Quote the fee for session installation
    /// @param intent The session installation intent
    /// @param options LayerZero execution options for each destination
    /// @param payInLzToken Whether to pay in LZ token
    function quoteInstallSessions(
        SessionIntent calldata intent,
        bytes[] calldata options,
        bool payInLzToken
    ) external view returns (MessagingFee memory totalFee) {
        require(intent.destinations.length == options.length, "Mismatched options length");

        bytes memory message = _encodeSessionMessage(intent);

        for (uint256 i = 0; i < intent.destinations.length; i++) {
            Destination memory dest = intent.destinations[i];
            bytes memory combinedOptions = combineOptions(dest.chainEid, INSTALL_SESSION, options[i]);

            MessagingFee memory fee = _quote(dest.chainEid, message, combinedOptions, payInLzToken);
            totalFee.nativeFee += fee.nativeFee;
            totalFee.lzTokenFee += fee.lzTokenFee;
        }
    }

    /// @notice Verify EIP-712 signature
    /// @param intent The session intent
    /// @param signature The signature to verify
    function _verifySessionIntent(
        SessionIntent calldata intent,
        bytes calldata signature
    ) internal view {
        bytes32 structHash = _hashSessionIntent(intent);
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, structHash));

        address recoveredSigner = digest.recover(signature);
        if (recoveredSigner != intent.user) revert InvalidSignature();
    }

    /// @notice Hash SessionIntent struct
    /// @param intent The session intent
    function _hashSessionIntent(SessionIntent calldata intent) internal pure returns (bytes32) {
        bytes32[] memory destinationHashes = new bytes32[](intent.destinations.length);
        for (uint256 i = 0; i < intent.destinations.length; i++) {
            destinationHashes[i] = keccak256(abi.encode(
                DESTINATION_TYPEHASH,
                intent.destinations[i].chainEid,
                intent.destinations[i].escrowAddr
            ));
        }

        bytes32[] memory tokenPolicyHashes = new bytes32[](intent.tokenPolicies.length);
        for (uint256 i = 0; i < intent.tokenPolicies.length; i++) {
            tokenPolicyHashes[i] = keccak256(abi.encode(
                TOKEN_POLICY_TYPEHASH,
                intent.tokenPolicies[i].token,
                intent.tokenPolicies[i].limit
            ));
        }

        return keccak256(abi.encode(
            SESSION_INTENT_TYPEHASH,
            intent.user,
            intent.sessionPubKey,
            intent.expiry,
            intent.nonce,
            keccak256(abi.encodePacked(destinationHashes)),
            keccak256(abi.encodePacked(tokenPolicyHashes))
        ));
    }

    /// @notice Install session locally on Sepolia
    /// @param intent The session intent
    function _installLocalSession(SessionIntent calldata intent) internal {
        // Generate deterministic wallet
        bytes32 salt = walletFactory.generateSalt(intent.user, block.chainid);
        address wallet = walletFactory.deployIfNeeded(intent.user, salt);

        // Find Sepolia escrow in destinations
        address sepoliaEscrow = address(0);
        for (uint256 i = 0; i < intent.destinations.length; i++) {
            if (intent.destinations[i].chainEid == 40161) { // SEPOLIA_V2_TESTNET
                sepoliaEscrow = intent.destinations[i].escrowAddr;
                break;
            }
        }

        if (sepoliaEscrow != address(0)) {
            // Convert policies to session module format
            SessionModule.TokenLimit[] memory tokenLimits = new SessionModule.TokenLimit[](intent.tokenPolicies.length);
            for (uint256 i = 0; i < intent.tokenPolicies.length; i++) {
                tokenLimits[i] = SessionModule.TokenLimit({
                    token: intent.tokenPolicies[i].token,
                    limit: intent.tokenPolicies[i].limit
                });
            }

            SessionModule.EscrowPermission[] memory escrowPermissions = new SessionModule.EscrowPermission[](1);
            escrowPermissions[0] = SessionModule.EscrowPermission({
                escrow: sepoliaEscrow,
                allowed: true
            });

            // Install session via wallet
            SessionModule sessionModule = walletFactory.sessionModule();
            sessionModule.installSession(
                wallet,
                intent.sessionPubKey,
                intent.expiry,
                tokenLimits,
                escrowPermissions
            );

            emit SessionInstalledLocally(intent.user, wallet);
        }
    }

    /// @notice Send cross-chain session installations
    /// @param intent The session intent
    /// @param options Execution options for each destination
    function _sendCrossChainInstallations(
        SessionIntent calldata intent,
        bytes[] calldata options
    ) internal {
        bytes memory message = _encodeSessionMessage(intent);
        uint256 remainingValue = msg.value;

        for (uint256 i = 0; i < intent.destinations.length; i++) {
            Destination memory dest = intent.destinations[i];

            // Skip if destination is current chain (already handled locally)
            if (dest.chainEid == 40161) continue; // Skip SEPOLIA_V2_TESTNET

            bytes memory combinedOptions = combineOptions(dest.chainEid, INSTALL_SESSION, options[i]);
            MessagingFee memory fee = _quote(dest.chainEid, message, combinedOptions, false);

            require(remainingValue >= fee.nativeFee, "Insufficient fee");
            remainingValue -= fee.nativeFee;

            _lzSend(
                dest.chainEid,
                message,
                combinedOptions,
                fee,
                payable(msg.sender)
            );
        }
    }

    /// @notice Encode session message for cross-chain transmission
    /// @param intent The session intent
    function _encodeSessionMessage(SessionIntent calldata intent) internal pure returns (bytes memory) {
        return abi.encode(
            intent.user,
            intent.sessionPubKey,
            intent.expiry,
            intent.destinations,
            intent.tokenPolicies
        );
    }

    /// @notice Handle incoming cross-chain messages (not used in this architecture)
    /// @param _origin Message origin
    /// @param _message The message payload
    function _lzReceive(
        Origin calldata _origin,
        bytes32 /*_guid*/,
        bytes calldata _message,
        address /*_executor*/,
        bytes calldata /*_extraData*/
    ) internal override {
        // This coordinator only sends messages, doesn't receive them
        // Could be used for confirmations or callbacks in future versions
    }

    /// @notice Get user's current nonce
    /// @param user The user address
    function getUserNonce(address user) external view returns (uint256) {
        return userNonces[user];
    }
}

contracts/Installer.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { OApp, Origin, MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { OAppOptionsType3 } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OAppOptionsType3.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { WalletFactory } from "./WalletFactory.sol";
import { SessionModule } from "./SessionModule.sol";

/// @title Installer - Receives cross-chain session installation messages
/// @notice Deployed on Hedera and Flow testnets to install session keys
contract Installer is OApp, OAppOptionsType3 {
    /// @notice The wallet factory for creating minimal wallets
    WalletFactory public immutable walletFactory;

    /// @notice Events
    event SessionInstalled(
        address indexed user,
        address indexed wallet,
        address indexed sessionPubKey,
        uint256 expiry,
        uint32 sourceChainEid
    );
    event MessageReceived(uint32 sourceChainEid, bytes32 sender, bytes message);

    /// @notice Errors
    error InvalidMessage();
    error SessionExpired();

    /// @notice Initialize Installer
    /// @param _endpoint LayerZero endpoint address
    /// @param _owner Contract owner
    /// @param _walletFactory Wallet factory contract
    constructor(
        address _endpoint,
        address _owner,
        WalletFactory _walletFactory
    ) OApp(_endpoint, _owner) Ownable(_owner) {
        walletFactory = _walletFactory;
    }

    /// @notice Handle incoming session installation messages
    /// @param _origin Message origin metadata
    /// @param _message The encoded session installation message
    function _lzReceive(
        Origin calldata _origin,
        bytes32 /*_guid*/,
        bytes calldata _message,
        address /*_executor*/,
        bytes calldata /*_extraData*/
    ) internal override {
        emit MessageReceived(_origin.srcEid, _origin.sender, _message);

        // Decode the session installation message
        (
            address user,
            address sessionPubKey,
            uint256 expiry,
            Destination[] memory destinations,
            TokenPolicy[] memory tokenPolicies
        ) = abi.decode(_message, (address, address, uint256, Destination[], TokenPolicy[]));

        // Verify session hasn't expired
        if (expiry <= block.timestamp) revert SessionExpired();

        // Install the session on this chain
        _installSession(
            user,
            sessionPubKey,
            expiry,
            destinations,
            tokenPolicies,
            _origin.srcEid
        );
    }

    /// @notice Install session on the current chain
    /// @param user The user address
    /// @param sessionPubKey The session public key
    /// @param expiry Session expiration timestamp
    /// @param destinations All destination configurations
    /// @param tokenPolicies Token spending policies
    /// @param sourceChainEid Source chain endpoint ID
    function _installSession(
        address user,
        address sessionPubKey,
        uint256 expiry,
        Destination[] memory destinations,
        TokenPolicy[] memory tokenPolicies,
        uint32 sourceChainEid
    ) internal {
        // Generate deterministic wallet address
        bytes32 salt = walletFactory.generateSalt(user, block.chainid);
        address wallet = walletFactory.deployIfNeeded(user, salt);

        // Find escrow address for current chain
        address escrowAddr = _findEscrowForCurrentChain(destinations);
        require(escrowAddr != address(0), "Escrow not found for current chain");

        // Convert token policies to session module format
        SessionModule.TokenLimit[] memory tokenLimits = new SessionModule.TokenLimit[](tokenPolicies.length);
        for (uint256 i = 0; i < tokenPolicies.length; i++) {
            tokenLimits[i] = SessionModule.TokenLimit({
                token: tokenPolicies[i].token,
                limit: tokenPolicies[i].limit
            });
        }

        // Set up escrow permissions (only the designated escrow is allowed)
        SessionModule.EscrowPermission[] memory escrowPermissions = new SessionModule.EscrowPermission[](1);
        escrowPermissions[0] = SessionModule.EscrowPermission({
            escrow: escrowAddr,
            allowed: true
        });

        // Install session via session module
        SessionModule sessionModule = walletFactory.sessionModule();
        sessionModule.installSession(
            wallet,
            sessionPubKey,
            expiry,
            tokenLimits,
            escrowPermissions
        );

        emit SessionInstalled(
            user,
            wallet,
            sessionPubKey,
            expiry,
            sourceChainEid
        );
    }

    /// @notice Find escrow address for the current chain
    /// @param destinations Array of destination configurations
    /// @return escrowAddr The escrow address for current chain
    function _findEscrowForCurrentChain(
        Destination[] memory destinations
    ) internal view returns (address escrowAddr) {
        uint32 currentEid = _getCurrentChainEid();

        for (uint256 i = 0; i < destinations.length; i++) {
            if (destinations[i].chainEid == currentEid) {
                return destinations[i].escrowAddr;
            }
        }

        return address(0);
    }

    /// @notice Get current chain's LayerZero endpoint ID
    /// @return The endpoint ID for current chain
    function _getCurrentChainEid() internal view returns (uint32) {
        // Map chain IDs to LayerZero V2 endpoint IDs
        if (block.chainid == 296) return 40285; // Hedera testnet
        if (block.chainid == 545) return 40351; // Flow testnet
        return 0; // Unknown chain
    }

    /// @notice Destination chain configuration (copied from Coordinator for decoding)
    struct Destination {
        uint32 chainEid;      // LayerZero endpoint ID
        address escrowAddr;   // Escrow contract address on destination
    }

    /// @notice Token spending policy (copied from Coordinator for decoding)
    struct TokenPolicy {
        address token;        // Token address (address(0) for native)
        uint256 limit;        // Spending limit
    }

    /// @notice Emergency function to manually install session (owner only)
    /// @param user The user address
    /// @param sessionPubKey The session public key
    /// @param expiry Session expiration timestamp
    /// @param escrowAddr The escrow address for this chain
    /// @param tokenLimits Token spending limits
    function emergencyInstallSession(
        address user,
        address sessionPubKey,
        uint256 expiry,
        address escrowAddr,
        SessionModule.TokenLimit[] calldata tokenLimits
    ) external onlyOwner {
        bytes32 salt = walletFactory.generateSalt(user, block.chainid);
        address wallet = walletFactory.deployIfNeeded(user, salt);

        SessionModule.EscrowPermission[] memory escrowPermissions = new SessionModule.EscrowPermission[](1);
        escrowPermissions[0] = SessionModule.EscrowPermission({
            escrow: escrowAddr,
            allowed: true
        });

        SessionModule sessionModule = walletFactory.sessionModule();
        sessionModule.installSession(
            wallet,
            sessionPubKey,
            expiry,
            tokenLimits,
            escrowPermissions
        );

        emit SessionInstalled(user, wallet, sessionPubKey, expiry, 0);
    }
}

contracts/MinimalWallet.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SessionModule } from "./SessionModule.sol";

/// @title MinimalWallet - Simple smart account with session key support
/// @notice A minimal smart wallet that can be controlled by owner or authorized session keys
contract MinimalWallet {
    /// @notice The owner of this wallet
    address public immutable owner;

    /// @notice The session module managing session keys
    SessionModule public immutable sessionModule;

    /// @notice Events
    event Executed(address indexed target, uint256 value, bytes data, bool success);
    event SessionExecuted(address indexed target, address indexed token, uint256 amount, bool success);

    /// @notice Errors
    error OnlyOwnerOrSession();
    error ExecutionFailed();
    error InsufficientBalance();

    /// @notice Initialize the wallet
    /// @param _owner The wallet owner
    /// @param _sessionModule The session module contract
    constructor(address _owner, SessionModule _sessionModule) {
        owner = _owner;
        sessionModule = _sessionModule;
    }

    /// @notice Execute a transaction from this wallet (owner only)
    /// @param target The target contract
    /// @param value The ETH value to send
    /// @param data The call data
    function execute(
        address target,
        uint256 value,
        bytes calldata data
    ) external returns (bool success, bytes memory returnData) {
        if (msg.sender != owner) revert OnlyOwnerOrSession();

        (success, returnData) = target.call{value: value}(data);
        emit Executed(target, value, data, success);

        return (success, returnData);
    }

    /// @notice Execute a transaction via session key
    /// @param target The target contract (must be approved escrow)
    /// @param token The token being transferred (address(0) for native)
    /// @param amount The amount being transferred
    /// @param data The call data
    /// @param signature The session signature
    function executeAsWallet(
        address target,
        address token,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) external payable returns (bool success, bytes memory returnData) {
        // Verify session through session module
        bool sessionValid = sessionModule.verifySession(
            address(this),
            target,
            token,
            amount,
            data,
            signature
        );

        require(sessionValid, "Session verification failed");

        // Execute the call from this wallet with proper value forwarding
        if (token == address(0) && amount > 0) {
            // For native token deposits, forward the received ETH
            require(msg.value >= amount, "Insufficient ETH sent");
            (success, returnData) = target.call{value: amount}(data);
        } else {
            // For ERC-20 or zero-value calls
            (success, returnData) = target.call(data);
        }

        emit SessionExecuted(target, token, amount, success);
        return (success, returnData);
    }

    /// @notice Transfer ERC-20 tokens (owner only)
    /// @param token The token contract
    /// @param to The recipient
    /// @param amount The amount to transfer
    function transferToken(
        IERC20 token,
        address to,
        uint256 amount
    ) external {
        if (msg.sender != owner) revert OnlyOwnerOrSession();
        require(token.transfer(to, amount), "Transfer failed");
    }

    /// @notice Transfer ETH (owner only)
    /// @param to The recipient
    /// @param amount The amount to transfer
    function transferETH(address payable to, uint256 amount) external {
        if (msg.sender != owner) revert OnlyOwnerOrSession();
        if (address(this).balance < amount) revert InsufficientBalance();

        (bool success,) = to.call{value: amount}("");
        require(success, "ETH transfer failed");
    }

    /// @notice Approve token spending (owner only)
    /// @param token The token contract
    /// @param spender The spender
    /// @param amount The amount to approve
    function approveToken(
        IERC20 token,
        address spender,
        uint256 amount
    ) external {
        if (msg.sender != owner) revert OnlyOwnerOrSession();
        require(token.approve(spender, amount), "Approval failed");
    }

    /// @notice Install a session key (owner only, delegates to session module)
    /// @param sessionPubKey The session public key
    /// @param expiry Session expiration timestamp
    /// @param tokenLimits Array of token spending limits
    /// @param escrowPermissions Array of allowed escrow contracts
    function installSession(
        address sessionPubKey,
        uint256 expiry,
        SessionModule.TokenLimit[] calldata tokenLimits,
        SessionModule.EscrowPermission[] calldata escrowPermissions
    ) external {
        if (msg.sender != owner) revert OnlyOwnerOrSession();

        sessionModule.installSession(
            address(this),
            sessionPubKey,
            expiry,
            tokenLimits,
            escrowPermissions
        );
    }

    /// @notice Revoke session key (owner only)
    function revokeSession() external {
        if (msg.sender != owner) revert OnlyOwnerOrSession();
        sessionModule.revokeSession(address(this));
    }

    /// @notice Get session info
    function getSession() external view returns (
        address sessionPubKey,
        uint256 expiry,
        bool isActive
    ) {
        return sessionModule.getSession(address(this));
    }

    /// @notice Receive ETH
    receive() external payable {}

    /// @notice Fallback function
    fallback() external payable {}
}

contracts/SessionModule.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { MessageHashUtils } from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

/// @title SessionModule - Manages session keys for minimal wallets
/// @notice Enables time-limited, policy-restricted execution via session keys
contract SessionModule {
    using ECDSA for bytes32;

    struct SessionPolicy {
        address sessionPubKey;     // Session public key for signature verification
        uint256 expiry;           // Timestamp when session expires
        mapping(address => uint256) tokenLimits; // Per-token spend caps
        mapping(address => bool) allowedEscrows;  // Allowed escrow contracts
        bool isActive;            // Session active status
    }

    struct TokenLimit {
        address token;
        uint256 limit;
    }

    struct EscrowPermission {
        address escrow;
        bool allowed;
    }

    /// @notice Mapping from wallet address to session policy
    mapping(address => SessionPolicy) public sessions;

    /// @notice Events
    event SessionInstalled(address indexed wallet, address indexed sessionPubKey, uint256 expiry);
    event SessionRevoked(address indexed wallet);
    event SessionUsed(address indexed wallet, address indexed token, uint256 amount);

    /// @notice Errors
    error SessionExpired();
    error SessionNotActive();
    error InvalidSignature();
    error TokenLimitExceeded();
    error EscrowNotAllowed();
    error OnlyWalletOwner();

    /// @notice Install a session key with policy restrictions
    /// @param wallet The wallet address this session controls
    /// @param sessionPubKey The session public key
    /// @param expiry Session expiration timestamp
    /// @param tokenLimits Array of token spending limits
    /// @param escrowPermissions Array of allowed escrow contracts
    function installSession(
        address wallet,
        address sessionPubKey,
        uint256 expiry,
        TokenLimit[] calldata tokenLimits,
        EscrowPermission[] calldata escrowPermissions
    ) external {
        // Only wallet itself can install sessions (called by Installer or wallet owner)
        if (msg.sender != wallet) revert OnlyWalletOwner();

        SessionPolicy storage policy = sessions[wallet];
        policy.sessionPubKey = sessionPubKey;
        policy.expiry = expiry;
        policy.isActive = true;

        // Set token limits
        for (uint256 i = 0; i < tokenLimits.length; i++) {
            policy.tokenLimits[tokenLimits[i].token] = tokenLimits[i].limit;
        }

        // Set escrow permissions
        for (uint256 i = 0; i < escrowPermissions.length; i++) {
            policy.allowedEscrows[escrowPermissions[i].escrow] = escrowPermissions[i].allowed;
        }

        emit SessionInstalled(wallet, sessionPubKey, expiry);
    }

    /// @notice Revoke an active session
    /// @param wallet The wallet address
    function revokeSession(address wallet) external {
        if (msg.sender != wallet) revert OnlyWalletOwner();
        sessions[wallet].isActive = false;
        emit SessionRevoked(wallet);
    }

    /// @notice Verify session signature and enforce policy (verification only)
    /// @param wallet The wallet address
    /// @param target The target contract address (must be allowed escrow)
    /// @param token The token being transferred (address(0) for native)
    /// @param amount The amount being transferred
    /// @param data The call data
    /// @param signature The session signature
    function verifySession(
        address wallet,
        address target,
        address token,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) external returns (bool) {
        SessionPolicy storage policy = sessions[wallet];

        // Check session is active and not expired
        if (!policy.isActive) revert SessionNotActive();
        if (block.timestamp > policy.expiry) revert SessionExpired();

        // Check escrow is allowed
        if (!policy.allowedEscrows[target]) revert EscrowNotAllowed();

        // Check token limit
        if (amount > 0) {
            if (policy.tokenLimits[token] < amount) revert TokenLimitExceeded();
            // Decrease available limit
            policy.tokenLimits[token] -= amount;
        }

        // Verify signature
        bytes32 messageHash = keccak256(abi.encodePacked(
            wallet, target, token, amount, data, block.chainid
        ));
        bytes32 ethSignedMessageHash = MessageHashUtils.toEthSignedMessageHash(messageHash);
        address recoveredSigner = ethSignedMessageHash.recover(signature);
        if (recoveredSigner != policy.sessionPubKey) revert InvalidSignature();

        // Session verification successful - update limits
        if (amount > 0) {
            emit SessionUsed(wallet, token, amount);
        }

        return true;
    }

    /// @notice Get session info
    /// @param wallet The wallet address
    function getSession(address wallet) external view returns (
        address sessionPubKey,
        uint256 expiry,
        bool isActive
    ) {
        SessionPolicy storage policy = sessions[wallet];
        return (policy.sessionPubKey, policy.expiry, policy.isActive);
    }

    /// @notice Get token limit for a wallet
    /// @param wallet The wallet address
    /// @param token The token address
    function getTokenLimit(address wallet, address token) external view returns (uint256) {
        return sessions[wallet].tokenLimits[token];
    }

    /// @notice Check if escrow is allowed
    /// @param wallet The wallet address
    /// @param escrow The escrow address
    function isEscrowAllowed(address wallet, address escrow) external view returns (bool) {
        return sessions[wallet].allowedEscrows[escrow];
    }
}

contracts/WalletFactory.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.22;

import { MinimalWallet } from "./MinimalWallet.sol";
import { SessionModule } from "./SessionModule.sol";

/// @title WalletFactory - Factory for deterministic minimal wallet deployment
/// @notice Creates minimal wallets with predictable addresses using CREATE2
contract WalletFactory {
    /// @notice The session module used by all wallets
    SessionModule public immutable sessionModule;

    /// @notice Mapping to track deployed wallets
    mapping(address => bool) public isWallet;

    /// @notice Events
    event WalletDeployed(address indexed owner, address indexed wallet, bytes32 salt);

    /// @notice Errors
    error WalletAlreadyExists();

    /// @notice Initialize the factory
    /// @param _sessionModule The session module contract
    constructor(SessionModule _sessionModule) {
        sessionModule = _sessionModule;
    }

    /// @notice Deploy a wallet if it doesn't exist
    /// @param owner The wallet owner
    /// @param salt The salt for CREATE2 deployment
    /// @return wallet The wallet address
    function deployIfNeeded(address owner, bytes32 salt) external returns (address wallet) {
        wallet = getWalletAddress(owner, salt);

        // If wallet doesn't exist, deploy it
        if (!isContract(wallet)) {
            MinimalWallet deployedWallet = new MinimalWallet{salt: salt}(owner, sessionModule);
            wallet = address(deployedWallet);
            isWallet[wallet] = true;

            emit WalletDeployed(owner, wallet, salt);
        }

        return wallet;
    }

    /// @notice Get the deterministic wallet address
    /// @param owner The wallet owner
    /// @param salt The salt for CREATE2
    /// @return wallet The predicted wallet address
    function getWalletAddress(address owner, bytes32 salt) public view returns (address wallet) {
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                address(this),
                salt,
                keccak256(abi.encodePacked(
                    type(MinimalWallet).creationCode,
                    abi.encode(owner, sessionModule)
                ))
            )
        );

        return address(uint160(uint256(hash)));
    }

    /// @notice Generate a salt based on owner and chain
    /// @param owner The wallet owner
    /// @param chainId The chain ID
    /// @return salt The generated salt
    function generateSalt(address owner, uint256 chainId) external pure returns (bytes32 salt) {
        return keccak256(abi.encodePacked(owner, chainId));
    }

    /// @notice Check if address is a contract
    /// @param account The address to check
    /// @return True if account is a contract
    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
}
